//Numro de Pedidos
function number_pedido($op=true)
{
	$ano=date("Y");
	$arquivo = $ano."pedido.txt"; 
	$fp = fopen ( $arquivo, "r" ); // Abre o arquivo somente para leitura e gravacao
	$cod = fread( $fp, filesize( $arquivo ) ); //Armazena o contedo do arquivo na variavel "$cod"
	fclose ( $fp ); // fecha a conexo com o arquivo
	if($op)
	{	//Imprimi o pedido	
		$resultado="$cod/$ano";
		return $resultado;
	}
	else
	{	//Soma 1 ao numero do pedido e grava no arquivo TXT
		$abre2 = fopen($arquivo, "w");
		$soma=sprintf("%06d",$cod+1);
		fwrite($abre2, $soma);
		fclose ($abre2);	
	}
}
